INSERT INTO hotels (name, location, description, images, breakfast_included, stars) VALUES
    ('Sunset Resort', 'Playa del Inglés, Gran Canaria', 'Un lujoso complejo hotelero ubicado en la famosa Playa del Inglés, con vistas impresionantes al océano Atlántico.', ARRAY['https://dynamic-media-cdn.tripadvisor.com/media/photo-o/25/2a/c2/96/sunset-resort.jpg?w=700&h=-1&s=1'], false, 0),
    ('Palm Oasis Maspalomas', 'Maspalomas, Gran Canaria', 'Un tranquilo oasis tropical situado en el corazón de Maspalomas, con jardines exuberantes y piscinas relucientes.', ARRAY['https://cf.bstatic.com/xdata/images/hotel/max1024x768/16370849.jpg?k=518e1b06c94ae7f97513726b7c2dc0d599042be7ba26c0d3666cdf6826a56491&o=&hp=1'], false, 0),
    ('Seaside Grand Hotel Residencia', 'Meloneras, Gran Canaria', 'Un hotel exclusivo de estilo colonial ubicado en la prestigiosa zona de Meloneras, ofreciendo un ambiente de lujo y servicio de primera clase.', ARRAY['https://cf.bstatic.com/xdata/images/hotel/max1024x768/500126856.jpg?k=b96239c9c882f8ac104cfc01144df8e51d105609752fb26771bfbf21dab0146c&o=&hp=1'], false, 0),
    ('Lopesan Costa Meloneras Resort', 'Meloneras, Gran Canaria', 'Un resort de cinco estrellas situado frente al mar, con una amplia gama de instalaciones y servicios para unas vacaciones inolvidables.', ARRAY['https://cf.bstatic.com/xdata/images/hotel/max1024x768/356490996.jpg?k=28e6c975b91671f883e5fe51e4673119242776af876a5726357927c196fdbaec&o=&hp=1'], false, 0),
    ('Hotel Riu Palace Oasis', 'Maspalomas, Gran Canaria', 'Un elegante hotel de estilo canario rodeado de palmeras y situado a pocos pasos de las dunas de Maspalomas.', ARRAY['https://dynamic-media-cdn.tripadvisor.com/media/photo-o/15/66/b2/cb/outdoor-pool.jpg?w=700&h=-1&s=1'], false, 0),
    ('Cordial Mogan Playa', 'Puerto de Mogán, Gran Canaria', 'Un encantador complejo de estilo colonial rodeado de exuberantes jardines subtropicales, ideal para unas vacaciones relajantes junto al mar.', ARRAY['https://cf.bstatic.com/xdata/images/hotel/max1024x768/507895886.jpg?k=b1d4ff6ff00222ac0d3008c8a2ac6e1d92b2aae9d4309533687c28af6f4ef3f7&o=&hp=1'], false, 0),
    ('Radisson Blu Resort & Spa', 'Puerto de Mogán, Gran Canaria', 'Un lujoso resort frente al océano con un spa de clase mundial y una amplia gama de actividades para toda la familia.', ARRAY['https://grancanariagolf.com/wp-content/uploads/2020/02/452-scaled.jpg'], false, 0),
    ('Lopesan Baobab Resort', 'Meloneras, Gran Canaria', 'Un resort de lujo inspirado en África, con impresionantes piscinas, exuberantes jardines y una variedad de restaurantes temáticos.', ARRAY['https://dynamic-media-cdn.tripadvisor.com/media/photo-o/0e/5e/48/da/lopesan-baobab-resort.jpg?w=700&h=-1&s=1'], false, 0),
    ('Gloria Palace Amadores Thalasso & Hotel', 'Puerto Rico, Gran Canaria', 'Un hotel de bienestar y thalasso con vistas panorámicas al océano Atlántico, ideal para unas vacaciones de relax y rejuvenecimiento.', ARRAY['https://cf.bstatic.com/xdata/images/hotel/max1024x768/313322542.jpg?k=fb08b3ad7e0dc90bdf4d296efd5d3e85d65c421a24c84d6d7b9a71547644decf&o=&hp=1'], false, 0);
